import React, { useState } from "react";

const Question = ({ question, selectedAnswer, onAnswer }) => {
  const [userInput, setUserInput] = useState("");
  const [submitted, setSubmitted] = useState(false);

  const handleAnswerSelection = (option) => {
    if (!submitted) {
      onAnswer(option);
      setSubmitted(true);
    }
  };

  const handleInputChange = (e) => {
    setUserInput(e.target.value);
  };

  const handleSaveAnswer = () => {
    if (!submitted) {
      onAnswer(userInput);
      setSubmitted(true);
    }
  };

  return (
    <div className="bg-white shadow-lg rounded-lg p-6 max-w-xl mx-auto w-full mt-6">
      <h2 className="text-lg font-bold text-gray-700 mb-2">{question.type}</h2>
      <h3 className="text-xl font-semibold text-gray-800 mb-4">{question.question}</h3>
      
      {question.options ? (
        <ul className="space-y-4">
          {question.options.map((option, index) => {
            let bgColor = "bg-gray-100 hover:bg-gray-200";
            if (submitted) {
              if (option === question.correctAnswer) {
                bgColor = "bg-green-500 text-white";
              } else if (option === selectedAnswer) {
                bgColor = "bg-red-500 text-white";
              }
            }
            return (
              <li
                key={index}
                className={`flex items-center p-3 rounded-lg shadow-sm transition cursor-pointer ${bgColor}`}
                onClick={() => handleAnswerSelection(option)}
              >
                <input
                  type="radio"
                  id={`option-${index}`}
                  name="answer"
                  value={option}
                  checked={selectedAnswer === option}
                  readOnly
                  className="mr-3 w-5 h-5 hidden"
                />
                <label htmlFor={`option-${index}`} className="text-gray-700 font-medium cursor-pointer">
                  {String.fromCharCode(65 + index)}. {option}
                </label>
              </li>
            );
          })}
        </ul>
      ) : (
        <div className="flex flex-col gap-4">
          <input
            type="text"
            value={userInput}
            onChange={handleInputChange}
            disabled={submitted}
            placeholder="Enter your answer"
            className={`w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400 ${submitted ? (userInput === question.correctAnswer ? 'bg-green-500 text-white' : 'bg-red-500 text-white') : ''}`}
          />
          <button
            onClick={handleSaveAnswer}
            disabled={submitted || userInput === ""}
            className="px-6 py-2 bg-blue-500 text-white font-semibold rounded-lg shadow-md hover:bg-blue-600 disabled:bg-gray-400"
          >
            Save
          </button>
          {submitted && userInput !== question.correctAnswer && (
            <p className="p-3 bg-green-500 text-white rounded-md">Correct Answer: {question.correctAnswer}</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Question;
