import React, { useState } from "react";

const Question = ({ question, selectedAnswer, onAnswer, onNext }) => {
  const [userInput, setUserInput] = useState("");
  const [submitted, setSubmitted] = useState(false);

  const handleAnswerSelection = (option) => {
    if (!submitted) {
      onAnswer(option);
    }
  };

  const handleInputChange = (e) => {
    setUserInput(e.target.value);
  };

  const handleSaveAndNext = () => {
    if (!submitted) {
      setSubmitted(true);
      onNext();
    }
  };

  return (
    <div className="bg-white shadow-lg rounded-lg p-6 max-w-xl mx-auto w-full mt-6">
      <div className="flex justify-between items-center mb-4">
        <h3 className="text-xl font-semibold text-gray-800">
          {question.question}
        </h3>
      </div>

      {question.options ? (
        <ul className="space-y-4">
          {question.options.map((option, index) => (
            <li
              key={index}
              className="flex items-center bg-gray-100 p-3 rounded-lg shadow-sm hover:bg-gray-200 transition"
            >
              <input
                type="radio"
                id={`option-${index}`}
                name="answer"
                value={option}
                checked={selectedAnswer === option}
                onChange={() => handleAnswerSelection(option)}
                className="mr-3 w-5 h-5 text-blue-500 focus:ring-blue-400"
              />
              <label
                htmlFor={`option-${index}`}
                className="text-gray-700 font-medium cursor-pointer"
              >
                {String.fromCharCode(65 + index)}. {option}
              </label>
            </li>
          ))}
        </ul>
      ) : (
        <div className="flex flex-col gap-4">
          <input
            type="text"
            value={userInput}
            onChange={handleInputChange}
            disabled={submitted}
            placeholder="Enter your answer"
            className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
        </div>
      )}

      <button
        onClick={handleSaveAndNext}
        disabled={!selectedAnswer && userInput === ""}
        className={`mt-4 px-6 py-2 font-semibold rounded-lg shadow-md transition 
          ${selectedAnswer || userInput !== "" ? "bg-blue-500 text-white hover:bg-blue-600" : "bg-gray-400 text-gray-700 cursor-not-allowed"}
        `}
      >
        Save and Next
      </button>
    </div>
  );
};

export default Question;
