import React, { useState, useEffect } from "react";

const Scoreboard = ({ score, totalQuestions, onRetry }) => {
  const [quizHistory, setQuizHistory] = useState([]);

  useEffect(() => {
    const saveScore = async () => {
      try {
        const db = await openDB();
        const tx = db.transaction("quizHistory", "readwrite");
        const store = tx.objectStore("quizHistory");
        const newEntry = {
          score,
          totalQuestions,
          date: new Date().toLocaleString(),
        };
        await store.add(newEntry);
        fetchHistory();
      } catch (error) {
        console.error("Error saving score:", error);
      }
    };

    saveScore();
  }, [score]);

  const openDB = async () => {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open("QuizDB", 1);
      request.onupgradeneeded = (event) => {
        const db = event.target.result;
        if (!db.objectStoreNames.contains("quizHistory")) {
          db.createObjectStore("quizHistory", { autoIncrement: true });
        }
      };
      request.onsuccess = () => {
        resolve(request.result);
        fetchHistory();
      };
      request.onerror = () => reject(request.error);
    });
  };

  const fetchHistory = async () => {
    try {
      const db = await openDB();
      const tx = db.transaction("quizHistory", "readonly");
      const store = tx.objectStore("quizHistory");
      const allRecords = await store.getAll();
      setQuizHistory(allRecords.reverse());
    } catch (error) {
      console.error("Error fetching history:", error);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6 text-center">
      <h2 className="text-3xl font-bold text-gray-800">Quiz Completed!</h2>
      <p className="text-xl font-semibold text-gray-700 mt-4">
        Your Score: {score} / {totalQuestions}
      </p>
      <div className="flex flex-wrap justify-center gap-2 mt-6">
        {Array.from({ length: totalQuestions }).map((_, index) => (
          <div
            key={index}
            className={`w-10 h-10 flex items-center justify-center rounded-full text-white font-bold text-sm shadow-md ${
              index < score ? "bg-green-500" : "bg-red-500"
            }`}
          >
            {index + 1}
          </div>
        ))}
      </div>
      <div className="flex items-center justify-center gap-4 mt-4">
        <span className="text-green-600 font-bold">✔ Correct</span>
        <span className="text-red-600 font-bold">✖ Incorrect</span>
      </div>

      <button
        onClick={onRetry}
        className="mt-6 px-6 py-3 text-lg font-semibold text-white bg-blue-500 hover:bg-blue-600 rounded-lg shadow-md focus:outline-none focus:ring-4 focus:ring-blue-300"
      >
        Reattempt Quiz
      </button>

      {/* Score History Section */}
      <div className="mt-10 w-full max-w-lg bg-white shadow-lg rounded-lg p-6">
        <h3 className="text-2xl font-semibold text-gray-800 mb-4">Score History</h3>
        {quizHistory.length > 0 ? (
          <ul className="space-y-4">
            {quizHistory.map((entry, index) => (
              <li
                key={index}
                className={`p-4 rounded-lg shadow-md border-l-8 transition duration-300 ease-in-out transform hover:scale-105 ${
                  entry.score >= entry.totalQuestions / 2 ? "border-green-500 bg-green-50" : "border-red-500 bg-red-50"
                }`}
              >
                <p className="text-lg font-semibold text-gray-800">Attempt {quizHistory.length - index}</p>
                <p className="text-sm text-gray-600">Date: {entry.date}</p>
                <p className="text-lg font-medium mt-1">
                  Score: <span className="font-bold text-gray-900">{entry.score} / {entry.totalQuestions}</span>
                </p>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-gray-500">No previous attempts found.</p>
        )}
      </div>
    </div>
  );
};

export default Scoreboard;
